//pyramid pattern
public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int r = 5;
		for(int i = 0; i <= r; ++i) {
			for(int j = 1; j <= i; ++j) {
			System.out.print("+ ");
		}
		System.out.println();

	}

}
}

/* output:

+ 
+ + 
+ + + 
+ + + + 
+ + + + + 

*/
